import { useEffect, useState } from "react";
import styled from "styled-components";
import { NavLink } from "react-router-dom";
import { addFavorite, deleteFavorite } from "../../redux/actions";
import { connect } from "react-redux";


const Container = styled.div`
    width: 230px;
    display: flex;
    margin: auto;
    margin-bottom: 20px;
    flex-direction: column;
    border: solid green 7px;
    position: relative;
    box-shadow: 10px 5px rgb(10, 10, 10);
    border-radius: 10px 10px 10px 10px;
`;
const Boton = styled.button`
    background-color:rgb(36, 128, 194);
    font-family: monospace;
    font-weight:600;
    color:black;
    font-size:15px;
    letter-spacing:1px;
    position:relative;
    display:inline-block;
    transition: 0.3s;
    border: solid;
    height: 40px;
    text-transform: uppercase;
`;
const Imagen = styled.img`
  border-radius: 1px;
  box-shadow: 0 0 33px black;
`;
const Nombre = styled.h2`
  background-color: white;
  margin: 0.7rem;
`;
const Caracteristicas = styled.h3`
  margin: 0.5rem;
  align-self: flex-start;
  border: 1px solid black;
  border-radius: 5px;
  padding: 0.3rem;
  min-width: 5rem;
  background-color: white;
`;

export function Card(props) {
  const [isFav, setIsFav] = useState(false);

  function handleFavorite() {
    if (isFav) {
      setIsFav(false)
      props.deleteFavorite(props.id)
    } else {
      setIsFav(true)
      const character = {
        key: props.id,
        id: props.id,
        name: props.name,
        image: props.image,
        species: props.species,
        gender: props.gender,
      };
      props.addFavorite(character)
    }
  }

  useEffect(() => {
    props.myFavorites?.forEach((fav) => {
      if (fav.id === props.id) {
        setIsFav(true);
      }
    });
  }, [props.id, props.myFavorites]);
  return (
    <Container>
      <>
        {isFav ? (
          <button onClick={handleFavorite}>‚ù§Ô∏è</button>
        ) : (
          <button onClick={handleFavorite}>ü§ç</button>
        )}
      </>
      <Boton onClick={props.onClose}>X</Boton>
      <Imagen src={props.image} alt={props.name} />
      <NavLink to={`/detail/${props.id}`}>
        <center><Nombre>{props.name}</Nombre></center>
      </NavLink>
      <center><Caracteristicas>{props.species}</Caracteristicas></center>
      <center><Caracteristicas>{props.gender}</Caracteristicas></center>
    </Container>
  );
}

export function mapStateToProps(state) {
  return {
    myFavorites: state.myFavorites
  }
}

export function mapDispatchToProps(dispatch) {
  return {
    addFavorite: function (fav) {
      dispatch(addFavorite(fav))
    },
    deleteFavorite: function (id) {
      dispatch(deleteFavorite(id))
    }
  };
};



export default connect(mapStateToProps, mapDispatchToProps)(Card);
